    src: url("../fonts/Roboto-Medium.ttf");
}
body {
    font-family: "Roboto-Medium";
}
.container {
    min-width: 320px;
    .container-mask {
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: rgba(0,0,0, .4);
        z-index: 1;
        display: none;
    }
    .left {
        width: 220px;
        background-color: $left-color;
        position: fixed;
        height: 100%;
        text-align: center;
        z-index: 2;
        .logo {
            display: inline-block;
            background-color: $color-white;
            color: $main-color;
            padding: 10px 19px;
            margin: 34px 0;
            font-size: 24px;
            font-weight: bold;
            border-radius: 0 14px 0 14px;
            &:hover {
                background-color: $main-color;
                color: $color-white;
            }
        }
        nav {
            ul {
                li {
                    &:nth-of-type(1) {
                        margin-top: 68px;
                    }
                    &.active {
                        a {
                            color: $main-color;
                        }
                        .icon1 {
                            display: none;
                        }
                        .icon2 {
                            display: block;
                        }
                    }
                    margin-bottom: 72px;
                    a {
                        color: $color-white;
                        font-size: 20px;
                        font-weight: bold;
                        .icon {
                            width: 30px;
                            margin: 0 25px 0 34px;
                            img {
                                width: 100%;
                            }
                        }
                        .icon2 {
                            display: none;
                        }
                    }
                }
            }
        }
        .logout {
            position: absolute;
            bottom: 50px;
            left: 50%;
            transform: translate(-50%, 0);
            a {
                font-size: 20px;
                color: $color-white;
                font-weight: bold;
                img {
                    width: 30px;
                    margin-right: 20px;
                    &.logout-img2 {
                        display: none;
                    }
                }
                &:hover {
                    color: $main-color;
                    img {
                        &.logout-img1 {
                            display: none;
                        }
                        &.logout-img2 {
                            display: block;
                        }
                    }
                }
            }
        }
    }
    .right {
        width: calc(100% - 220px);
        margin-left: 220px;
        .toolbar {
            padding: 0 64px 0 38px;
            height: 94px;
            box-shadow: 0 2px 10px #e8e8e8;
            .page-title{
                color: $main-color;
                font-size: 20px;
                font-weight: bold;
            }
            form {
                width: 416px;
                border: 1px solid #e4e4e4;
                border-radius: 8px;
