                        margin-top: 20px;
                        span {
                            display: inline-block;
                            font-size: 20px;
                            color: #281976;
                            font-weight: bold;
                            margin-right: 20px;
                        }
                        input[type=checkbox] {
                            cursor: pointer;
                            position: relative;
                            width: 15px;
                            height: 15px;
                            font-size: 14px;
                            &::after {
                                position: absolute;
                                top: 0;
                                color: #000;
                                width: 15px;
                                height: 15px;
                                display: inline-block;
                                visibility: visible;
                                padding-left: 0px;
                                text-align: center;
                                content: ' ';
                                border-radius: 3px
                            }
                            &:checked::after {
                                content: "✓";
                                color: #fff;
                                font-size: 12px;
                                font-weight: bold;
                                background-color: #281976;
                            }
                        }
                    }
                    .pom-set {
                        margin-top: 20px;
                        text-align: center;
                        display: none;
                        .pom-set-title {
                            span {
                                display: inline-block;
                                width: 180px;
                                font-size: 20px;
                                color: #281976;
                                font-weight: bold;
                            }
                        }
                        .pom-set-inp {
                            margin-top: 10px;
                            input {
                                text-align: center;
                                width: 180px;
                                line-height: 60px;
                                font-size: 20px;
                                color: #281976;
                                font-weight: bold;
                                border-radius: 20px;
                            }
                        }
                    }
                }
            }
            // This is the flashcards page
            &.flashcards {
                background: url("../images/background.png") center center no-repeat;
                background-size: 100% 100%;
                .transparency {
                    overflow: scroll;
                }
                .flashcards-top {
                    margin: 42px 50px 24px;
                    button {
                        width: 100px;
                        line-height: 40px;
                        font-size: 20px;
                        border-radius: 5px;
                        background: $left-color;
                        color: #fff;
                        margin-left: 20px;
                    }
                }
                .flashcards-form {
                    width: 510px;
                    background-color: #a8bcfe;
                    border-radius: 30px;
                    margin: 0 auto;
                    padding-bottom: 20px;
                    display: none;
                    .flashcards-form-title {
                        text-align: center;
                        font-size: 30px;
                        color: #6b5ddf;
                        line-height: 62px;
                        border-bottom: 6px solid #6b5ddf;
                    }
                    .form-row {
                        width: 90%;
                        margin: 10px auto 0;
                        label {
                            display: block;
                            font-size: 20px;
                            color: #7265e3;
                        }
                        input {
                            font-size: 20px;
                            margin-top: 10px;
                            padding: 0 2%;
                            width: 96%;
                            line-height: 48px;
                            border-radius: 5px;
                        }
                        button {
                            font-size: 16px;
                            color: #fff;
                            background-color: #ea8b8b;
                            line-height: 36px;
                            padding: 0 10px;
                            border-radius: 5px;
                        }
                    }
                }
                .flashcards-list {
                    padding: 0 50px;
                    overflow-y: scroll;
                    .flashcards-item {
                        border-radius: 30px;
                        background-color: #dee6ff;
                        margin-top: 20px;
                        width: 32%;
                        margin-right: 2%;
                        height: 260px;
                        &.active {
                            p {
                                &:nth-of-type(2) {
                                    display: block;
                                }
                            }
                        }
                        &:nth-of-type(3n) {
                            margin-right: 0;
                        }
                        .flashcards-item-head {
                            height: 60px;
                            border-bottom: 6px solid #4b8af8;
                        }
                        p {
                            font-size: 20px;
                            line-height: 32px;
                            padding: 4px 20px;
                            &:nth-of-type(1) {
                                color: #4b8af8;
                            }
                            &:nth-of-type(2) {
                                color: #80abfb;
                                display: none;
                            }
                        }
                    }
                }
            }
            .transparency {
                position: absolute;
                top: 43px;
                left: 62px;
                width: calc(100% - 124px);
                height: calc(100vh - 94px - 86px);
                background: rgba(255,255,255, .7);
                // This is the readinglist page
                .readinglist-top {
                    margin: 42px 100px 24px;
                    .readinglist-top-le {
                        input,
                        button {
                            line-height: 40px;
                            font-size: 20px;
                            border-radius: 5px;
                        }
                        input {
                            background-color: $left-color;
                            color: $color-white;
                            width: 154px;
							position: relative;
							&::-webkit-calendar-picker-indicator {
								opacity:0;
								width: 25px;
								height: 25px;
								position: relative;
								z-index:1;
							}
							&::after{
							    content: '';
							    position: absolute;
							    right: 6px;
							    top: 50%;
								transform: translate(0,-50%);
							    width: 22px;
							    height: 22px;
							    background: url("../images/arrow-down.png") no-repeat center center;
								background-size: 100% 100%;
							}
							&::-webkit-inner-spin-button {
								visibility: hidden;
							}
                        }
                        button {
                            width: 116px;
                            border: 1px solid $left-color;
                            color: $left-color;
                            margin-left: 20px;
                            background: none;
                            box-sizing: border-box;
                        }
                    }
                    .readinglist-top-re {
                        button {
                            width: 110px;
                            line-height: 40px;
                            font-size: 20px;
                            border-radius: 5px;
                            background: $left-color;
                            color: #fff;
                            margin-left: 20px;
                        }
                    }
                }
                .readinglist-content {
                    margin: 0 100px;
                    background-color: #fff;
                    padding: 40px;
                    p {
                        font-size: 20px;
                        color: $main-color;
                        text-align: center;
                    }
                    #list {
                        height: 300px;
                        overflow: hidden;
                    }
                    ul {
                        li {
                            display: flex;
                            align-items: center;
                            padding: 0 20px;
                            background-color: #f9f8f8;
                            line-height: 52px;
                            border-radius: 8px;
                            position: relative;
                            margin-top: 20px;
                            input[type="checkbox"] {
                                margin-right: 10px;
                                width: 20px;
                                height:20px;
                                appearance: none;
                                position: relative;
                                &:before{
                                    content: '';
                                    width: 20px;
                                    height: 20px;
                                    border: 1px solid #707070;
                                    display: inline-block;
                                    vertical-align: middle;
                                 }
                                 &:checked:before{
                                    content: '';
                                    width: 20px;
                                    height: 20px;
                                    border: 1px solid #707070;
                                    display: inline-block;
                                    vertical-align: middle;
                                 }
                                 &:checked:after{
                                    content: '';
                                    width: 12px;
                                    height: 12px;
                                    text-align: center;
                                    background:#707070;
                                    display: block;
                                    position: absolute;
                                    top: 5px;
                                    left: 5px;
                                 }
                                 &:checked+label{
                                    color: #707070;
                                 }
                            }
                            span{
                               font-size: 15px;
                               color: #707070; 
                            }
                            input[type="text"] {
                                font-size: 15px;
                                color: #707070; 
                                background: none;
                                line-height: 52px;
                                flex: 1;
                                margin-right: 20px;
                            }
                            a {
                                font-size: 15px;
                                color: #707070; 
                                word-break: keep-all;
                                white-space: nowrap;
                                overflow: hidden;
                                text-overflow: ellipsis;
                               &:hover {
                                    color: $main-color;
                                }
                            }
                            .del-item {
                                position: absolute;
                                right: 10px;
                                cursor: pointer;
                            }
                        }
                    }
                    .addList {
                        a {
                            font-size: 20px;
                            color: #a096fa;
                        }
                        .arrow {
                            margin-left: 6px;
                            width: 8px;
                            height: 8px;
                            border-left: 2px solid #a096fa;
                            border-bottom: 2px solid #a096fa;
                            transform: rotate(-45deg);
                        }
                    }
                    .readinglist-form {
                        margin-top: 10px;
                        textarea {
                            width: 96%;
                            background-color: #f9f8f8;
                            border-radius: 8px;
                            resize: none;
                            padding: 10px 2%;
                            height: 90px;
                        }
                        button {
                            width: 65px;
                            line-height: 30px;
                            border-radius: 5px;
                            background-color: $left-color;
                            color: $color-white;
                            margin-top: 10px;
                            &:nth-of-type(2) {
                                opacity: .6;
                            }
                        }
                    }
                }
            }
        }
    }
}
.modal {
	position: fixed;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	display: none;
	.mask {
		position: absolute;
		top: 0;
		left: 0;
		width: 100%;
		height: 100%;
		background-color: rgba(0,0,0, .4);
		z-index: 1;
	}
	.modal-task {
		position: absolute;
		left: 50%;
		top: 50%;
		transform: translate(-50%, -50%);
		background-color: #fff;
		width: 66%;
		z-index: 2;
		.modal-head {
			background-color: $left-color;
			line-height: 60px;
			padding: 0 20px;
			span {
				color: #fff;
				font-size: 20px;
				&#close {
					cursor: pointer;
				}
			}
		}
		.modal-body {
			background-color: $color-white;
			padding: 40px;
			text-align: center;
			.row {
				width: 40%;
				margin: 12px auto;
				label {
					font-size: 16px;
					color: #707070;
				}
				input,
				textarea,
				select{
					border: 1px solid #e9e9e9;
					border-radius: 5px;
					font-size: 16px;
					margin-top: 10px;
					line-height: 40px;
					width: 96%;
					padding: 0 2%;
				}
				select {
					height: 40px;
					width: 100%;
				}
				textarea {
					padding: 2%;
					resize: none;
				}
				input[name="time"] {
					width: auto;
				}
				input[name="startDate"] {
					margin-right: 4%;
				}
				input[name="startDate"],
				input[name="dueDate"] {
					position: relative;
					&::-webkit-calendar-picker-indicator {
						opacity:0;
						width: 25px;
						height: 25px;
						position: relative;
						z-index:1;
					}
					&::after{
						content: '';
						position: absolute;
						right: 4px;
						top: 10px;
						width: 22px;
						height: 22px;
						background: url("../images/triangle.png") no-repeat center center;
						background-size: 100% 100%;
					}
					&::-webkit-inner-spin-button {
						visibility: hidden;
					}
				}
				
				button {
					font-size: 14px;
					padding: 10px 20px;
					border-radius: 5px;
					background: none;
					&:nth-of-type(1) {
						color: $left-color;
						border: 1px solid $left-color;
					}
					&:nth-of-type(2){
						color: $color-white;
						background: $left-color;
					}
				}
			}
		}
	}
}
